def load_intents():
    file_path = os.path.join(os.path.dirname(__file__), "intents.xlsx")
    df = pd.read_excel(file_path)
    
    intents = {}
    for _, row in df.iterrows():
        patterns = row["Patterns"].split("; ")  # Split multiple patterns
        responses = row["Responses"].split("; ")  # Split multiple responses
        for pattern in patterns:
            intents[pattern.lower()] = responses  # Store possible responses
    
    return intents

INTENTS = load_intents()
THRESHOLD = 90  # Increase confidence level for rule-based responses

from django.utils.safestring import mark_safe
import re

def format_bot_response(response_text):
    """Formats chatbot responses to be displayed cleanly in the UI."""
    
    # Remove unnecessary asterisks, bullet points, and special characters
    response_text = re.sub(r"[*â€¢]", "", response_text)

    # Convert multiple newlines into paragraph breaks
    response_text = re.sub(r"\n\s*\n", "<br><br>", response_text)

    # Ensure lists are displayed properly
    response_text = response_text.replace("\n", "<br>")

    # Handle bold text if the API response contains **text**
    response_text = re.sub(r"\*\*(.*?)\*\*", r"<strong>\1</strong>", response_text)

    return mark_safe(response_text)  # Ensures it renders safely in Django templates

def chatbots(request):
    # âœ… **Retrieve chat history (last 10 messages)**
    if request.user.is_authenticated:
        chat_history = ChatHistory.objects.filter(user=request.user).order_by('-timestamp')[:10]
    else:
        chat_history = ChatHistory.objects.filter(user=None).order_by('-timestamp')[:10]

    if request.method == "POST":
        user_input = request.POST.get("message", "").strip().lower()

        # âœ… **Check if response is cached (faster performance)**
        cached_response = cache.get(user_input)
        if cached_response:
            bot_response = cached_response
        else:
            # âœ… **Use fuzzy matching but favor Gemini API**
            best_match, score = process.extractOne(user_input, INTENTS.keys())

            if score >= THRESHOLD:
                bot_response = random.choice(INTENTS[best_match])  # Use high-confidence rule-based response
            else:
                # âœ… **Retrieve past chat history for context**
                past_chats = [
                    f"User: {entry.user_query}\nBot: {entry.bot_response}"
                    for entry in chat_history
                ]
                chat_context = "\n".join(past_chats)

                # âœ… **Send query to Gemini API with past context**
                prompt = f"Context:\n{chat_context}\n\nNew User Query: {user_input}"
                try:
                    response = model.generate_content(prompt)
                    bot_response = response.text if response else "I'm not sure. Please check the college website."
                except Exception as e:
                    bot_response = "Sorry, I am unable to process your request at the moment."

            # âœ… **Cache response for 1 hour**
            cache.set(user_input, bot_response, timeout=3600)

        # âœ… **Format bot response before storing**
        bot_response = format_bot_response(bot_response)

        # âœ… **Store conversation in the database**
        if request.user.is_authenticated:
            ChatHistory.objects.create(user=request.user, user_query=user_input, bot_response=bot_response)
        else:
            ChatHistory.objects.create(user=None, user_query=user_input, bot_response=bot_response)

        # âœ… **Prepare chat history for display (reversed for chronological order)**
        chat_history_display = [
            {
                "user_query": entry.user_query,
                "bot_response": entry.bot_response,
                "time": entry.timestamp.strftime("%Y-%m-%d %H:%M:%S"),
            }
            for entry in reversed(chat_history)
        ]

        # âœ… **Include current chat in the display**
        chat_history_display.append({
            "user_query": user_input,
            "bot_response": bot_response,
            "time": datetime.now().strftime("%Y-%m-%d %H:%M:%S"),
        })

        return render(request, "chat/chat.html", {"chat_history": chat_history_display})

    # âœ… **Prepare chat history for GET request (default)**
    chat_history_display = [
        {
            "user_query": entry.user_query,
            "bot_response": entry.bot_response,
            "time": entry.timestamp.strftime("%Y-%m-%d %H:%M:%S"),
        }
        for entry in reversed(chat_history)
    ]

    return render(request, "chat/chat.html", {"chat_history": chat_history_display})

